    var link = _edit.getLinkJson(parentNodeObj, newNodeData, 10);
    var newNodeObj = _edit.appendNode(parentNodeObj.uri(), newNodeData,
                                      link, parentNodeObj.attributes()); 
    var oldID = newNodeObj.attributes()["id"]
