var db_get = '/db/get?id=';
var db_save_data = '/db/save/data?enc=0';
var db_save_node = '/db/save/node?enc=0';
var hidedChooseFile = $('<input type="file" id="hided-choose-file"/>');

main();

// Update action style.
function updateStyle() {
    _this.$el.find('.info').css('font-size', '15px');
    var exp = _this.$el.find('.exp');
    exp.html('');
    exp.css('background-image', 'url(vendor/assets/asterisk.svg)');
    exp.css('width', '10px');
    exp.css('height', '10px');
};

function chooseFileChange() {
    hidedChooseFile.bind("change", function(e) {
        //TODO: Handle if selected more than one file to upload
        var formdata = new FormData();
        var fileinfo = {};
        formdata.append(1, this.files[0]);
        fileinfo.name = e.currentTarget.files[0].name;
        fileinfo.size = e.currentTarget.files[0].size;
        fileinfo.type = e.currentTarget.files[0].type;
        fileinfo.lastModified = e.currentTarget.files[0].lastModified;
        fileinfo.path = URL.createObjectURL(e.currentTarget.files[0]);
        var isSVG = (-1 != (fileinfo.type).indexOf('svg+xml'));
        var data = null;
        if (isSVG) {
            var reader = new FileReader();
            reader.onload = function () {
                data = this.result;
                saveLeviathanRequest(data, fileinfo, isSVG);
            };
            reader.readAsDataURL(this.files[0]);
        } else {
            saveLeviathanRequest(formdata, fileinfo);
        }
    });
}

function saveLeviathanRequest (formdata, fileinfo, svg) {
    var url = _hostLeviathan + (svg ? db_save_node : db_save_data);
    $.ajax({
        type : 'POST',
        url : url,
        cache: false,
        contentType: false,
        processData: false,
        dataType: "json",
        data: formdata,
        success : function(data) {
            if(data && ! data['error']) {
                debugger;
                var msg = "Saved " + fileinfo.name + " by uuid: " + data.uuid;
                console.log(msg);
                alert(msg);
                var nodeObj = _edit.clicked_node;
                var attr = nodeObj.attributes();
                var title = attr['title'];
                title.value = title.value ? title.value : fileinfo.name;
                var img = attr['img'];
                img.visible = '1';
                //img.value = _hostLeviathan + db_get + data.uuid;
                img.value = data.uuid;
                attr['tags'] = (attr['tags'] ? attr['tags'] + ',' : '' )
                               + fileinfo.type;

                var rootURI = window.vostan.getRootURI();
                var rootNode = _vmap.nodeByURI(rootURI);
                rootNode.modified = true;
                //Updates the added node id in its own json
                _edit.updateInfo(nodeObj);
            }
        },
        error : function(xhr) {
            console.error();
            var msg = "Failed save " + this.url;
            console.log(msg + ", " + xhr);
            alert(msg);
        }
    });
};


function registerClickEvent() {
    _this.$el.find('.view').unbind('click').bind('click', function(e) {
        hidedChooseFile.click();
    });
    chooseFileChange();
};

// main function.
function main() {
    updateStyle();
    $(document).on('edit-node-clicked', function(e) {
        _edit = e.edit;
    });
    registerClickEvent();
};

